void find(int a, int& b, int& c, int d) {
if(d<1)
return;
cout<<a<<","<<b<<","<<c<<endl;
c = a + 2 * b;
int temp = b;
b = a;
a = 2 * temp;
d%2?find(b,a,c,d-1):find(c,b,a,d-1);
}
