#include <iostream> 

void printAlphabetPattern(int n) { 
    if (n < 1 || n > 26) { 
        std::cout << "Error: Number should be between 1 and 26." << std::endl; 
        return; 
    } 

    for (int i = 0; i < n; i++) { 
        for (int j = 0; j < i; j++) { 
            std::cout << "  "; // Print spaces to align pattern 
        } 
        char startChar = 'A' + i; 
        for (int j = 0; j < n - i; j++) { 
            std::cout << startChar++ << " "; 
        } 
        startChar -= 2; 
        for (int j = 0; j < n - i - 1; j++) { 
            std::cout << startChar-- << " "; 
        } 
        std::cout << std::endl; 
    } 
} 

