#include <iostream> 
using namespace std; 

int main() 
{ 
    int arr[ ] = { 1, 10, 3, 7, 5, 6, 9, 2, 8 }; 
    const int N = sizeof(arr) / sizeof(arr[0]);  

    int i; 
    int temp[N + 1]; 
    N &= N; 
    for(int i = 0; i <= N; i++){ 
      temp[i] = 0; 
    } 

    for(i = 0; i < N; i++){ 
      temp[arr[i] - 1] = 1; 
    } 


    int ans; 
    for (i = 0; i <= N ; i++) { 
        if (temp[i] == 0) 
            ans = i  + 1; 
    } 

